// Service Worker for PWA
// This will be generated by vite-plugin-pwa in production

self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open('planning-timesheet-v1').then((cache) => {
      return cache.addAll([
        '/',
        '/index.html',
        '/src/main.jsx',
        '/src/index.css'
      ]);
    })
  );
});

self.addEventListener('fetch', (event) => {
  event.respondWith(
    caches.match(event.request).then((response) => {
      return response || fetch(event.request);
    })
  );
});
